function twitterCallback2(e){var t=[];for(var n=0;n<e.length;n++){var r=e[n].user.screen_name;var i=e[n].text.replace(/((https?|s?ftp|ssh)\:\/\/[^"\s\<\>]*[^.,;'">\:\s\<\>\)\]\!])/g,function(e){return'<a href="'+e+'">'+e+"</a>"}).replace(/\B@([_a-z0-9]+)/ig,function(e){return e.charAt(0)+'<a href="http://twitter.com/'+e.substring(1)+'">'+e.substring(1)+"</a>"});t.push("<li><span>"+i+'</span> <a style="font-size:85%" href="http://twitter.com/'+r+"/statuses/"+e[n].id_str+'">'+relative_time(e[n].created_at)+"</a></li>")}document.getElementById("twitter_update_list").innerHTML=t.join("")}function relative_time(e){var t=e.split(" ");e=t[1]+" "+t[2]+", "+t[5]+" "+t[3];var n=Date.parse(e);var r=arguments.length>1?arguments[1]:new Date;var i=parseInt((r.getTime()-n)/1e3);i=i+r.getTimezoneOffset()*60;if(i<60){return"less than a minute ago"}else if(i<120){return"about a minute ago"}else if(i<60*60){return parseInt(i/60).toString()+" minutes ago"}else if(i<120*60){return"about an hour ago"}else if(i<24*60*60){return"about "+parseInt(i/3600).toString()+" hours ago"}else if(i<48*60*60){return"1 day ago"}else{return parseInt(i/86400).toString()+" days ago"}}